import React, { useState } from "react";

export function CreateArea(props) {

    const [input, setInput] = useState({
        title: "",
        content: ""
    });

    function updateInput(event) {
        const {name, value} = event.target;
        setInput((prevInput) => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    }

    return (
        <div>
            <form>
                <input onChange={updateInput} name="title" placeholder="Title" />
                <textarea onChange={updateInput} name="content" placeholder="Take a note..." rows="3" />
                <button onClick={() => {
                    props.onAdd(input);
                }}>Add</button>
            </form>
        </div>
    );
}
